import './CBased.css';
import React, {Component} from "react";

class CBased extends Component{

	constructor() {
    super();
    this.state = {
      userName:'',
      token:'',
      songName:[],
      songID:[],
      limit:10
    }
  }

  getData (accessToken) {
    fetch('https://api.spotify.com/v1/me/top/tracks?limit=10', {
      method: 'GET',
      headers: {'Authorization': 'Bearer ' + accessToken,
                'Content-Type': 'application/json',}
    }).then(response => response.json())
    .then(data => this.setState(state => {
      //data.items.map(i => state.userData.push(!i.name))
      //console.log(data);
      data.items.map(function(i){
        if (!state.songName.includes(i.name) && !state.songID.includes(i.id)){
          state.songName.push(i.name);
          state.songID.push(i.id);
        }

        return state.songName && state.songID;
      })
    }));

    // Check if it retreives song name and id correctly
    //console.log(this.state.songName);
    //console.log(this.state.songID);
  }

  getUserData(accessToken){
  	// Fetch User's profile information
    fetch('https://api.spotify.com/v1/me', {
      method: 'GET',
      headers: {'Authorization': 'Bearer ' + accessToken}
    }).then(response => response.json())
    .then(data => this.setState({
      userName: data.display_name
    }));
  }

  componentDidMount() {
    const url = new URL(window.location.href);
    const accessToken = url.searchParams.get('access_token');
	  //console.log(accessToken);
	  this.setState({
        	token: accessToken
    });

    console.log(accessToken);

    if(accessToken){
    	this.getUserData(accessToken);
      this.getData(accessToken);
    };
  }

	render() {
		//console.log(Object.keys(this.state.songName).map(key => (this.state.songName[key])));
    //console.log(this.state.songName[1]);
    if(!this.state.token){
      console.log("LALALALLAALL");
      return 'la';
    }

    // This will render once all the specific results have been fetched
    return this.state.songName.map((song, i) => {
        return (
            <div key={i}>
                    1st API call data:
                    { song }
            </div>
        );
    });

    	// // return(
     // 		<div className="CB">
     //  			<h1 style={{'fontSize': '35px','marginTop': '5px' }}>Content Based Reccommendation!</h1>
     //  			<h1 style={{'fontSize': '15px','marginTop': '5px' }}>User top songs:  
     //          <div>
     //            {this.state.token}
     //          </div>
     //        </h1>
     //  	</div>

     // 	);
    }
}

export default CBased;
