	  //     	method: 'GET',
	  //     	headers: {'Authorization': 'Bearer ' + accessToken}
	  //   }).then(response => response.json())
	  //   .then(res => {
	  //   	const datas = res.items
	  //   	const id = []
	  //   	const ids = datas.map(datas => {
	  //   		if (!id.includes(datas.id)){
