import './App.css';
import React, { Component } from "react";
import Button from "./components/CustomButtonComponent";
import history from './history';
import Routes from './Routes';
import CBased from "./CBased/CBased";
import CFiltering from "./CFiltering/CollabFiltering";
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import update from 'react-addons-update'; 

class App extends Component {

  	constructor(props){
    	super(props);
    	this.state = {
      		token:'',
      		dataAudio:[],
      		isActive:false,
      		userName:'',
      		songName:[],
      		songID:[],
      		songID_str:'',
      		audio_feat:[],
      		limit:10				// cambiar le limit a 50!
    	}
  	}

  	getUserData(accessToken){
  		// Fetch User's profile information
    	fetch('https://api.spotify.com/v1/me', {
      		method: 'GET',
      		headers: {'Authorization': 'Bearer ' + accessToken}
    	}).then(response => response.json())
    	.then(data => this.setState({
      		userName: data.display_name
    	}));
  	}

  	getSongID(accessToken){
  		// Fetch User's most listened/top songs + songs id's
	    fetch('https://api.spotify.com/v1/me/top/tracks?limit=' + this.state.limit, {
	      	method: 'GET',
	      	headers: {'Authorization': 'Bearer ' + accessToken}
	    }).then(response => response.json())
	    .then(res => {
	    	const datas = res.items
	    	const id = []
	    	const ids = datas.map(datas => {
	    		if (!id.includes(datas.id)){
      				id.push(datas.id)
	        	};
	        	return id;
	    	})
	    	this.setState({
	    		songID: ids
	    	})

	    });
  	}

  	getAudioFeat(songIDS,accessToken){
	    // Get audio analysis features from the user's songs
	    fetch('https://api.spotify.com/v1/audio-features?ids=' + songIDS, {
	      method: 'GET',
	      headers: {'Authorization': 'Bearer ' + accessToken}
	    }).then(response => response.json())
	    .then(data => console.log(data));
	    //console.log("...................");
	    //console.log(this.state.songData);

  	}

  	componentDidMount() {
    	const url = new URL(window.location.href);
		const accessToken = url.searchParams.get('access_token');

	  	this.setState({
        	token: accessToken
    	});

    	if(accessToken){
    		this.getUserData(accessToken);
      		this.getSongID(accessToken);
      		this.getAudioFeat(this.state.songID.toString(), accessToken)
      	};
  	}


  	showHandler = ()=>{
      	this.setState({
        	isActive: true
      	})
  	}

  render(){
    return (
      <div className="App">
        <Routes />
        {this.state.isActive ? 

        	<div>
        	<header className="App-header">
	        <h1>Music Recommendation System Evaluation</h1>
	        <p>
	          Music recommendation system where users can choose which recommendation algorithm they enjoy better. 
	          This app is part of my final project for my degree in data science imparted by University Pompeu Fabra.
	        </p>
	        </header>
	        <br />
	        <br />
	        <h1 style={{'fontSize': '25px','marginTop': '5px', 'textAlign': 'left', 'border': '3px solid #9DC183', 'padding': '10px'}}>Content Based Recommendations for {this.state.userName} </h1>
	        <CBased songID={this.state.songID.toString()} token={this.state.token}/>

	        <br />
	        <br />
	        <h1 style={{'fontSize': '25px','marginTop': '5px', 'textAlign': 'left', 'border': '3px solid #9DC183', 'padding': '10px'}}>Collaborative Filtering Recommendations for {this.state.userName} </h1>


	        </div> : <button onClick={() => window.location='http://localhost:8888/login' }>Sign in with Spotify</button>                                                                      

        } 
        <div>{!this.state.isActive && <button onClick={this.showHandler}>Click here to continue</button>}</div>


      </div> 

    );
  }

}

//<button onClick={this.showHandler}>Click here to continue</button>

export default App;
