   //    				id.push(datas.id)
	  //       	};
	  //       	return id;
	  //   	})
	  //   	this.setState({
	  //   		songID: ids
	  //   	})

	  //   });
  	// }

  	getCB_Recom(songIDS,accessToken,genres,artistID){
	    // Get audio analysis features from the user's songs
	    const energy = [];      //low level feature
	    const key = [];         //high level feature
	    const loudness = [];    //low level feature -- can be negative
	    const acoustic = [];    //low level feature (?)
	    const instrum = [];     //low level feature (?)
	    const liveness = [];    //low level feature (?)
	    const valence = [];     //low level feature (?)
	    const tempo = [];       //high level feature
	    const duration = [];    //song duration in ms!

	    fetch('https://api.spotify.com/v1/audio-features?ids=' + songIDS.replace(",","%"), {
	      method: 'GET',
	      headers: {'Authorization': 'Bearer ' + accessToken}
	    }).then(response => response.json())
	    //.then(data => console.log(data.audio_features));
	    .then(data => {
	    	if(!data.audio_features){
	    		return;
	    	}else{
		      data.audio_features.map((audio, i) => {
		        energy.push(Number(audio.energy))
		        key.push(audio.key)
		        loudness.push(audio.loudness)
		        acoustic.push(audio.acousticness)
		        instrum.push(audio.instrumentalness)
		        liveness.push(audio.liveness)
		        valence.push(audio.valence)
		        tempo.push(audio.tempo)
		        duration.push(audio.duration_ms)
		      });
		      const avg_energy = energy.reduce((sum, curr) => sum + curr, 0)/energy.length ;            
		      const avg_key = key.reduce((sum, curr) => sum + curr, 0)/key.length ;                     
		      const avg_loud = loudness.reduce((sum, curr) => sum + curr, 0)/loudness.length ;          
		      const avg_acous = acoustic.reduce((sum, curr) => sum + curr, 0)/acoustic.length ;        
		      const avg_instr = instrum.reduce((sum, curr) => sum + curr, 0)/instrum.length ;           
		      const avg_liveness = liveness.reduce((sum, curr) => sum + curr, 0)/liveness.length ;      
		      const avg_val = valence.reduce((sum, curr) => sum + curr, 0)/valence.length ;
		      const avg_tempo = tempo.reduce((sum, curr) => sum + curr, 0)/tempo.length ;
		      const avg_duration = duration.reduce((sum, curr) => sum + curr, 0)/duration.length ;      

		      // Get the most popular genre of the user
		      const words = genres.split(',');
		      const wordCounts = { };

		      for(var i = 0; i < words.length; i++)
		        wordCounts[words[i]] = (wordCounts[words[i]] || 0) + 1;

		      const getMax = object => {
		        return Object.keys(object).filter(x => {
		             return object[x] == Math.max.apply(null, 
		             Object.values(object));
		        });
		      };

		      const userGenre = getMax(wordCounts).toString();
		      const song_seed = songIDS.split(','); //get user's top song for recommendation seed
		      console.log(artistID.replace(",","%"));
		      // Get 20 recommendations of content based for the user
		      // fetch("https://api.spotify.com/v1/recommendations?limit=20&market=ES&seed_artists=" + artistID + "&seed_genres=" + userGenre +
		      //           "&seed_tracks=" + song_seed[0] + "&target_acousticness=" + avg_acous + "&target_duration_ms=" + avg_duration + 
		      //           "&target_energy=" + avg_energy + "&target_instrumentalness=" + avg_instr + "&target_key=" + avg_key + "&target_liveness=" + avg_liveness +
		      //           "&target_loudness=" + avg_loud + "&target_tempo=" + avg_tempo + "&target_valence=" + avg_val, {
		      fetch("https://api.spotify.com/v1/recommendations?limit=20&market=ES&seed_artists=" + artistID.replace(",","%") + "&seed_genres=" + userGenre +
		                "&seed_tracks=" + song_seed[0], {
		          method: 'GET',
		          headers: {'Authorization': 'Bearer ' + accessToken}
		      }).then(response => response.json())
		      .then(data => console.log(data));

		      //console.log(promise_recom);
		    }
	    });

	    //console.log("...................");
	    //console.log(this.state.songData);

	}

  	componentDidMount() {
    	const url = new URL(window.location.href);
		const accessToken = url.searchParams.get('access_token');

	  	this.setState({
        	token: accessToken
    	});

    	if(!accessToken){
    		return;
    	}

    	// Fetch User's profile information
    	fetch('https://api.spotify.com/v1/me', {
      		method: 'GET',
      		headers: {'Authorization': 'Bearer ' + accessToken}
    	}).then(response => response.json())
    	.then(data => this.setState({
      		userName: data.display_name
    	}));

    	// Fetch User's most listened/top songs + songs id's
	    fetch('https://api.spotify.com/v1/me/top/tracks?limit=' + this.state.limit, {
	      	method: 'GET',
	      	headers: {'Authorization': 'Bearer ' + accessToken}
	    }).then(response => response.json())
	    .then(data => {
	    	let id = []
	    	let ids = data.items.map(datas => {
	    		if (!id.includes(datas.id)){
      				id.push(datas.id)
	        	};
	        	return id;
	    	})

	    	this.setState({
	    		songID: ids
	    	})
	    });

	    // Fetch User's top 3 artists -- nedeed for spotify's api content based recommendations
	    fetch('https://api.spotify.com/v1/me/top/artists?limit=3', {
	      	method: 'GET',
	      	headers: {'Authorization': 'Bearer ' + accessToken}
	    }).then(response => response.json())
	    .then(data => {
	    	if(!data.items){
	    		return;
	    	}
	    	let id = []
	    	let gen = []
	    	let ids = data.items.map(datas => {
	    		if (!id.includes(datas.id)){
      				id.push(datas.id)
	        	};
	        	return id;
	    	})

	    	let genre = data.items.map(datas => {
	    		if(!gen.includes(datas.genres)){
	    			gen.push(datas.genres)
	    		};

	    		this.setState({
	    			genres: gen.flat()
	    		});

	    		return gen;
	    	})

	    	this.setState({
	    		artistID: ids
	    	})


	    });

	    // songIDS,accessToken,genres,artistID
    	if(accessToken){
    		//this.getCB_Recom(this.state.songID.toString(),this.state.token, this.state.genres.toString(), this.state.artistID.toString())
      	};
  	}


  	showHandler = ()=>{
      	this.setState({
        	isActive: true
      	})
  	}

  render(){
    return (
      <div className="App">
        <Routes />
        {this.state.isActive ? 

        	<div>
        	<header className="App-header">
	        <h1>Music Recommendation System Evaluation</h1>
	        <p>
	          Music recommendation system where users can choose which recommendation algorithm they enjoy better. 
	          This app is part of my final project for my degree in data science imparted by University Pompeu Fabra.
	        </p>
	        </header>
	        <br />
	        <br />
	        <h1 style={{'fontSize': '25px','marginTop': '5px', 'textAlign': 'left', 'border': '3px solid #9DC183', 'padding': '10px'}}>Content Based Recommendations for {this.state.userName} </h1>
	        <CBased songID={this.state.songID.toString()} token={this.state.token} artistID={this.state.artistID.toString()} genres={this.state.genres.toString()}/>

	        <br />
	        <br />
	        <h1 style={{'fontSize': '25px','marginTop': '5px', 'textAlign': 'left', 'border': '3px solid #9DC183', 'padding': '10px'}}>Collaborative Filtering Recommendations for {this.state.userName} </h1>


	        </div> : <button onClick={() => window.location='http://localhost:8888/login' }>Sign in with Spotify</button>                                                                      

        } 
        <div>{!this.state.isActive && <button onClick={this.showHandler}>Click here to continue</button>}</div>


      </div> 

    );
  }

}

