import React, { Component } from "react";
import queryString from 'querystring';
import Button from "./../components/CustomButtonComponent";
import history from './../history';
import "./Home.css";

export default class Home extends Component {

  /* CallFunction_1(){
    //CB.render();
    window.open("/CollabFiltering");

    const script = document.createElement('script');
    script.src = "./CollabFiltering.js";
    script.async = true;
    document.body.appendChild(script);

    window.location.href = script;

    //window.open("./CollabFiltering.js");
    //window.reload("./CollabFiltering.js", true);
    //document.location.href = "./CollabFiltering.js";
  } */ 

  constructor() {
    super();
    this.state = {
      serverData: {},
      isActive:false
    }
  }

  // state = {
  //    isActive:false
  // }

  componentDidMount() {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    // if (!accessToken)
    //    return;
    fetch('https://api.spotify.com/v1/me', {
      headers: {'Authorization': 'Bearer' + accessToken}
    }).then(response => response.json()).then(data => this.setState({serverData: {user: {name: data.name}}}))

  }

  showHandler = ()=>{
      this.setState({
          isActive: true
      })
  }

  // hideHandler = () =>{
  //     this.setState({
  //         isActive: false
  //     })
  // }

  render() {
    return (
      <div className="Home">

        {this.state.isActive ? 
          <div>
            <h1>Music Recommendation System Evaluation</h1>
            <p>
              Music recommendation system where users can choose which recommendation algorithm they enjoy better. 
              This app is part of my final project for my degree in data science imparted by University Pompeu Fabra.
            </p>

            <h1 style={{'font-size': '15px','margin-top': '5px' }}>
            User name: {this.state.serverData.user.name} </h1>

            <Button
              border="none"
              color="pink"
              height = "200px"
              onClick={() => alert("CB Button")}
              radius = "50%"
              width = "200px"
              children = "Content Based Recommender System"
            />
            <div style={{width:"25%", display:'inline-block'}}/>
            <Button
              border="none"
              color="#9DC183"
              height = "200px"
              onClick={() => history.push('/CollabFiltering')}
              radius = "50%"
              width = "200px"
              children = "Collaborative Filtering"
            />

          </div> : <button onClick={() => window.location='http://localhost:8888/login' }>Sign in with Spotify</button>

          } 

          <button onClick={this.showHandler}>Click here to continue</button>

          {/* arreglar esto, esconder el bot√≥n o cambiarlo para que funcione con el api */}

    </div>

    );
  }
}
