import './CBased.css';
import React, {Component} from "react";

class CBased extends Component{

  getSongData(songIDS,accessToken,genres,artistID){
    // Get audio analysis features from the user's songs
    const energy = [];      //low level feature
    const key = [];         //high level feature
    const loudness = [];    //low level feature -- can be negative
    const acoustic = [];    //low level feature (?)
    const instrum = [];     //low level feature (?)
    const liveness = [];    //low level feature (?)
    const valence = [];     //low level feature (?)
    const tempo = [];       //high level feature
    const duration = [];    //song duration in ms!

    fetch('https://api.spotify.com/v1/audio-features?ids=748mdHapucXQri7IAO8yFK,3lIxtCaROdRDuTnNBDm3n2'/* + songIDS*/, {
      method: 'GET',
      headers: {'Accept': 'application/json', 'Content-Type': 'application/json' , 'Authorization': 'Bearer ' + accessToken,}
    }).then(response => response.json())
    //.then(data => console.log(data.audio_features));
    .then(data => {
      // if(!data.audio_features){
      //     return;
      //   }else{
          console.log(data.audio_features)
          data.audio_features.map((audio, i) => {
            // if(!audio){
            //   return;
            // }
            energy.push(Number(audio.energy))
            key.push(audio.key)
            loudness.push(audio.loudness)
            acoustic.push(audio.acousticness)
            instrum.push(audio.instrumentalness)
            liveness.push(audio.liveness)
            valence.push(audio.valence)
            tempo.push(audio.tempo)
            duration.push(audio.duration_ms)
          });
          const avg_energy = energy.reduce((sum, curr) => sum + curr, 0)/energy.length ;            //done
          const avg_key = key.reduce((sum, curr) => sum + curr, 0)/key.length ;                     //done
          const avg_loud = loudness.reduce((sum, curr) => sum + curr, 0)/loudness.length ;          //done
          const avg_acous = acoustic.reduce((sum, curr) => sum + curr, 0)/acoustic.length ;         //done  
          const avg_instr = instrum.reduce((sum, curr) => sum + curr, 0)/instrum.length ;           //done
          const avg_liveness = liveness.reduce((sum, curr) => sum + curr, 0)/liveness.length ;      //done
          const avg_val = valence.reduce((sum, curr) => sum + curr, 0)/valence.length ;
          const avg_tempo = tempo.reduce((sum, curr) => sum + curr, 0)/tempo.length ;
          const avg_duration = duration.reduce((sum, curr) => sum + curr, 0)/duration.length ;      //done

          // Get the most popular genre of the user
          const words = genres.split(',');
          const wordCounts = { };

          for(var i = 0; i < words.length; i++)
            wordCounts[words[i]] = (wordCounts[words[i]] || 0) + 1;

          const getMax = object => {
            return Object.keys(object).filter(x => {
                 return object[x] == Math.max.apply(null, 
                 Object.values(object));
            });
          };

          const userGenre = getMax(wordCounts).toString();
          const song_seed = songIDS.split(','); //get user's top song for recommendation seed

          // Get 20 recommendations of content based for the user
          // const promise_recom = fetch("https://api.spotify.com/v1/recommendations?limit=20&market=ES&seed_artists=" + artistID + "&seed_genres=" + userGenre +
          //           "&seed_tracks=" + song_seed[0] + "&target_acousticness=" + avg_acous + "&target_duration_ms=" + avg_duration + 
          //           "&target_energy=" + avg_energy + "&target_instrumentalness=" + avg_instr + "&target_key=" + avg_key + "&target_liveness=" + avg_liveness +
          //           "&target_loudness=" + avg_loud + "&target_tempo=" + avg_tempo + "&target_valence=" + avg_val, {
          //     method: 'GET',
          //     headers: {'Authorization': 'Bearer ' + accessToken}
          // }).then(response => response.json())
          // .then(data => console.log(data));

        //}

    });

    //console.log("...................");
    //console.log(this.state.songData);

  }




  // componentDidMount(){

  // }

  render() {

    let ids = this.props.songID
    //console.log(ids.replaceAll(',', '%'))
    let token = this.props.token
    console.log(token)
    let artistID = this.props.artistID.replaceAll(',', '%')
    let genres = this.props.genres
    if(token){
      let ene = this.getSongData(ids,token,genres,artistID)
    }


    return(
      <div>Audio Feat {ids}</div>

    );


  }

}

export default CBased;
